+        result += `${g.ind()}else => {\n`;
+        g.depth++;
+
+        for (const stmt of node.defaultCase.body) {
+            result += g.genStatement(stmt) + '\n';
+        }
+
+        g.depth--;
+        result += `${g.ind()}},\n`;
+    }
+
     g.depth--;
     result += `${g.ind()}}`;

