# =============================================================================
# EXPRESSION DISPATCH
# =============================================================================

# Generate C++ code for an expression
@ publica
functio genExpressia(Expressia expr, CppGenerator g) -> textus {
    discerne expr {
        # Identifiers
        casu Nomen ut e {
            redde e.valor qua textus
        }

        casu EgoExpressia {
            redde "this"
        }

        # Literals
        casu Littera ut e {
            redde genLittera(e.species qua LitteraGenus, e.crudus qua textus, g)
        }

        # Binary operations
        casu BinariaExpressia ut e {
            fixum sinister = genExpressia(e.sinister, g)
            fixum dexter = genExpressia(e.dexter, g)
            varia signum = e.signum qua textus

            # WHY: Normalize keyword operators to C++ equivalents.
            elige signum {
                casu "et" { signum = "&&" }
                casu "aut" { signum = "||" }
                casu "vel" {
                    # Nullish coalescing: a ?? b -> (a.has_value() ? *a : b) or (a ? a : b)
                    redde scriptum("(Â§ ? Â§ : Â§)", sinister, sinister, dexter)
                }
            }

            # WHY: Set membership lowers to find() in C++.
            si signum == "inter" {
                g.addeInclude("algorithm")
                redde scriptum("(std::find(Â§.begin(), Â§.end(), Â§) != Â§.end())", dexter, dexter, sinister, dexter)
            }

            redde scriptum("(Â§ Â§ Â§)", sinister, signum, dexter)
        }

        # Unary operations
        casu UnariaExpressia ut e {
            fixum arg = genExpressia(e.argumentum, g)

            # WHY: Null/value checks use appropriate C++ patterns.
            elige e.signum qua textus {
                casu "nihil" {
                    redde scriptum("(!Â§.has_value())", arg)
                }
                casu "nonnihil" {
                    redde scriptum("(Â§.has_value())", arg)
                }
                casu "negativum" {
                    redde scriptum("(Â§ < 0)", arg)
                }
                casu "positivum" {
                    redde scriptum("(Â§ > 0)", arg)
                }
                casu "verum" {
                    redde scriptum("(Â§ == true)", arg)
                }
                casu "falsum" {
                    redde scriptum("(Â§ == false)", arg)
                }
            }

            redde scriptum("Â§Â§", e.signum, arg)
        }

        # Assignment
        casu AssignatioExpressia ut e {
            redde scriptum("Â§ Â§ Â§", genExpressia(e.sinister, g), e.signum, genExpressia(e.dexter, g))
        }

        # Ternary conditional
        casu CondicioExpressia ut e {
            redde scriptum("Â§ ? Â§ : Â§", genExpressia(e.condicio, g), genExpressia(e.consequens, g), genExpressia(e.alternans, g))
        }

        # Range (C++23 ranges)
        casu AmbitusExpressia ut e {
            g.addeInclude("ranges")
            fixum initium = genExpressia(e.initium, g)
            fixum finis = genExpressia(e.finis, g)
            # std::views::iota for ranges
            si e.inclusivum qua bivalens {
                redde scriptum("std::views::iota(Â§, Â§ + 1)", initium, finis)
            }
            redde scriptum("std::views::iota(Â§, Â§)", initium, finis)
        }

        # Type cast
        casu QuaExpressia ut e {
            fixum cppExpr = genExpressia(e.expressia, g)
            fixum cppTypus = genTypus(e.scopus qua TypusAnnotatio, g)
