                        discerne paramLista[0] {
                            casu Typus ut t {
                                keyType = genTypus(t.adnotatio, g)
                            }
                            casu _ { }
                        }
                    }
                    si paramLista.longitudo() >= 2 {
                        discerne paramLista[1] {
                            casu Typus ut t {
